mapper.select:
SELECT *
FROM account_verification
WHERE CODE = #{code}
AND
TYPE = #{type}

mapper.update:
UPDATE
account_verification
SET VERIFIED = 'Y', USED_AT = NOW()
WHERE CODE =
#{code}
AND TYPE = #{type}

mapper.select:
SELECT * FROM client
WHERE CONCAT(email_id, '@',
email_domain) = #{email}

SELECT * FROM account_verification
WHERE phone = #{phone}
AND type = #{type}
AND verified = 'N'
ORDER BY created_at DESC
LIMIT 1

mapper.update:
UPDATE account_verification
SET
CODE = #{code},
EXPIRES_AT
= #{expiresAt}
WHERE
PHONE = #{phone}
AND TYPE = #{type}
AND VERIFIED =
'N'

UPDATE
account_verification
SET client_id = #{clientId}
WHERE phone = #{phone}
AND type = 'MEMBER_JOIN'

mapper.select:
SELECT verified
FROM account_verification
WHERE id = #{id}

mapper.update:
UPDATE account_verification
SET verified = 'Y'
WHERE id = #{id}
